preConditions.sqlCheck:
SELECT COUNT(*) FROM location location_duplicate INNER JOIN location location
WHERE location.locid > location_duplicate.locid AND location.labbr = location_duplicate.labbr
AND location.labbr NOT IN ("", "-");

changeSet.sql:
ALTER TABLE location ALTER labbr DROP DEFAULT;
UPDATE location SET labbr=NULL WHERE labbr IN ("", "-");
ALTER TABLE location ADD UNIQUE INDEX labbr_UNIQUE (labbr ASC);
