changeSet.sql:
ALTER TABLE `project` ADD COLUMN `dataset_type_id` INT(11) NULL DEFAULT NULL AFTER `study_type_id`;
ALTER TABLE `project` ADD INDEX `dataset_type_fk_idx` (`dataset_type_id` ASC);
ALTER TABLE `project` ADD CONSTRAINT `dataset_type_fk` FOREIGN KEY (`dataset_type_id`) REFERENCES `dataset_type` (`dataset_type_id`) ON DELETE NO ACTION ON UPDATE NO ACTION;
-- Populate project.dataset_type_id based on projectprop value (linking by dataset_type.cvterm_id) UPDATE project p INNER JOIN projectprop pp ON p.project_id = pp.project_id INNER JOIN dataset_type dt ON pp.value = dt.cvterm_id SET p.dataset_type_id = dt.dataset_type_id WHERE pp.variable_id = 8160;
