changeSet.sql:
CREATE TEMPORARY TABLE means_stock_id AS SELECT DISTINCT means_exp.stock_id FROM nd_experiment means_exp INNER JOIN project means ON means.project_id = means_exp.project_id AND means.dataset_type_id = 2 INNER JOIN project plot ON plot.study_id = means.study_id AND plot.dataset_type_id = 4 INNER JOIN nd_experiment plot_exp ON plot_exp.project_id = plot.project_id INNER JOIN stock means_stock ON means_stock.stock_id = means_exp.stock_id INNER JOIN stock plot_stock ON plot_stock.stock_id = plot_exp.stock_id WHERE means_stock.uniquename = plot_stock.uniquename AND means_exp.stock_id != plot_stock.stock_id;
UPDATE nd_experiment means_exp INNER JOIN project means ON means.project_id = means_exp.project_id AND means.dataset_type_id = 2 INNER JOIN project plot ON plot.study_id = means.study_id AND plot.dataset_type_id = 4 INNER JOIN nd_experiment plot_exp ON plot_exp.project_id = plot.project_id INNER JOIN stock means_stock ON means_stock.stock_id = means_exp.stock_id INNER JOIN stock plot_stock ON plot_stock.stock_id = plot_exp.stock_id SET means_exp.stock_id = plot_stock.stock_id WHERE means_stock.uniquename = plot_stock.uniquename AND means_exp.stock_id != plot_stock.stock_id;
DELETE s, sp FROM stock s LEFT JOIN stockprop sp ON s.stock_id = sp.stock_id WHERE s.stock_id IN ( SELECT stock_id FROM means_stock_id );

-- Migrate listdata_project to stock table. Only entry list from studies without plot experiments are migrated to the stock table. INSERT INTO stock (dbxref_id, name, uniquename, value, type_id, project_id) SELECT germplasm_id, designation, entry_id, entry_code, 8300, p.study_id FROM listnms lnms INNER JOIN listdata_project ldp ON lnms.listid = ldp.list_id INNER JOIN project p ON p.parent_project_id = lnms.projectid AND p.dataset_type_id = 4 WHERE lnms.listtype = 'STUDY' AND NOT EXISTS(SELECT 1 FROM nd_experiment where project_id = p.project_id);
-- Create check type property records for migrated stock entries. -- CHECK TYPE INSERT INTO stockprop (stock_id, type_id, value, rank) SELECT stock.stock_id, 8255, ldp.check_type, (SELECT IFNULL(MAX(rank), 0) + 1 FROM stockprop WHERE stock_id = stock.stock_id) FROM listnms lnms INNER JOIN listdata_project ldp ON lnms.listid = ldp.list_id INNER JOIN project p ON p.parent_project_id = lnms.projectid AND p.dataset_type_id = 4 INNER JOIN stock ON stock.project_id = p.study_id AND stock.uniquename = ldp.entry_id WHERE lnms.listtype = 'STUDY' AND NOT EXISTS(SELECT 1 FROM nd_experiment where project_id = p.project_id);
-- SEED_SOURCE INSERT INTO stockprop (stock_id, type_id, value, rank) SELECT stock.stock_id, 8360, ldp.seed_source, (SELECT IFNULL(MAX(rank), 0) + 1 FROM stockprop WHERE stock_id = stock.stock_id) FROM listnms lnms INNER JOIN listdata_project ldp ON lnms.listid = ldp.list_id INNER JOIN project p ON p.parent_project_id = lnms.projectid AND p.dataset_type_id = 4 INNER JOIN stock ON stock.project_id = p.study_id AND stock.uniquename = ldp.entry_id WHERE lnms.listtype = 'STUDY' AND NOT EXISTS(SELECT 1 FROM nd_experiment where project_id = p.project_id);
-- GROUPNAME/CROSS INSERT INTO stockprop (stock_id, type_id, value, rank) SELECT stock.stock_id, 8377, ldp.group_name, (SELECT IFNULL(MAX(rank), 0) + 1 FROM stockprop WHERE stock_id = stock.stock_id) FROM listnms lnms INNER JOIN listdata_project ldp ON lnms.listid = ldp.list_id INNER JOIN project p ON p.parent_project_id = lnms.projectid AND p.dataset_type_id = 4 INNER JOIN stock ON stock.project_id = p.study_id AND stock.uniquename = ldp.entry_id WHERE lnms.listtype = 'STUDY' AND NOT EXISTS(SELECT 1 FROM nd_experiment where project_id = p.project_id);
