changeSet.sql:
INSERT INTO germplasm_study_source(`gid`,`project_id`,`nd_experiment_id`,`type`) SELECT ldp.germplasm_id, lnms.projectid, NULL, CASE WHEN lnms.listtype IN ( 'IMP_CROSS', 'CRT_CROSS' ) THEN 'CROSS' ELSE 'ADVANCE' END FROM listnms lnms INNER JOIN listdata_project ldp ON lnms.listid = ldp.list_id WHERE lnms.listtype IN ( 'ADVANCED', 'IMP_CROSS', 'CRT_CROSS' );
-- Remove listdata_project_id reference in ims_stock_transaction table and replace it with sourceid that links to germplasm_study_source ALTER TABLE `ims_stock_transaction` ADD COLUMN `source_id` INT NULL AFTER `listdata_project_id`;
UPDATE ims_stock_transaction ist INNER JOIN listdata_project ldp ON ist.listdata_project_id = ldp.listdata_project_id INNER JOIN germplasm_study_source gss ON ldp.germplasm_id = gss.gid SET ist.source_id = gss.source_id;
ALTER TABLE `ims_stock_transaction` DROP COLUMN `listdata_project_id`, DROP INDEX `germplsm_transaction_idx1` , ADD UNIQUE INDEX `germplsm_transaction_idx1` (`trnid` ASC), ADD INDEX `FK_source_id_idx` (`source_id` ASC);
ALTER TABLE `ims_stock_transaction` ADD CONSTRAINT `FK_source_id_1` FOREIGN KEY (`source_id`) REFERENCES `germplasm_study_source` (`source_id`) ON DELETE NO ACTION ON UPDATE NO ACTION;
-- Remove all ADVANCE and CROSS LIST. DELETE ldp FROM listnms lnms INNER JOIN listdata_project ldp ON lnms.listid = ldp.list_id WHERE lnms.listtype IN ( 'ADVANCED', 'IMP_CROSS', 'CRT_CROSS' );
DELETE lnms FROM listnms lnms WHERE lnms.listtype IN ( 'ADVANCED', 'IMP_CROSS', 'CRT_CROSS' );
