changeSet.sql:
CREATE PROCEDURE populate_germplsm_uuid_values() BEGIN
DECLARE useUuid INT DEFAULT 1;
SELECT use_uuid INTO useUuid FROM workbench.workbench_crop WHERE db_name = DATABASE();
IF useUuid = 0 THEN
UPDATE germplsm g SET germplsm_uuid = CONCAT((SELECT plot_code_prefix FROM workbench.workbench_crop wc WHERE db_name = DATABASE()), 'G', RIGHT(sha1(CONCAT(uuid(), g.gid)), 15)) WHERE g.germplsm_uuid IS NULL;
ELSE
UPDATE germplsm g SET germplsm_uuid = uuid() WHERE g.germplsm_uuid IS NULL;
END IF;
END;
