INSERT INTO cvterm (cv_id, name, definition, dbxref_id, is_obsolete, is_relationshiptype)
VALUES (1040, 'ENTRY_SOURCE', 'Entry Source', null, 0, 0);

SET @cvterm_id  = (SELECT LAST_INSERT_ID());

