CREATE PROCEDURE check_listentry_sequence() BEGIN
DROP TABLE IF EXISTS list_seq_table;
CREATE TABLE list_seq_table ( seq INT(11) NOT NULL AUTO_INCREMENT, entryid INT(11) DEFAULT NULL, listid INT(11) DEFAULT NULL, lrecid INT(11) DEFAULT NULL, PRIMARY KEY (seq), KEY edx (entryid), KEY ldx (lrecid) ) ENGINE=INNODB;
INSERT INTO list_seq_table ( entryid, listid, lrecid ) SELECT entryid, l.listid, lrecid FROM listdata l , listnms n WHERE l.listid = n.listid AND l.listid IN (SELECT listid FROM listdata l GROUP BY listid HAVING MAX(entryid)!=COUNT(entryid)) ORDER BY l.listid, entryid ;
UPDATE listdata l, (SELECT lists.listid, val, ((((max_seq - lists.seq)-(max_seq-min_seq))*(-1))+1) AS list_seq , cnt, lrecid FROM (SELECT listid , MAX(seq) AS max_seq,MIN(seq) min_seq,MAX(entryid) max_val,MIN(entryid) AS min_val, COUNT(*) AS cnt FROM list_seq_table GROUP BY listid ) AS minmaxlist INNER JOIN (SELECT listid,seq, entryid AS val, lrecid FROM list_seq_table) AS lists ON lists.listid = minmaxlist.listid ORDER BY seq ) AS ordered_list SET l.entryid = ordered_list.list_seq WHERE l.listid = ordered_list.listid AND l.lrecid = ordered_list.lrecid AND l.entryid<>list_seq;
DROP TABLE IF EXISTS list_seq_table;
END;

CREATE PROCEDURE check_stockentry_sequence() BEGIN
DROP TABLE IF EXISTS stock_seq_table;
CREATE TABLE stock_seq_table( seq INT(11) NOT NULL AUTO_INCREMENT, entryid INT(11) DEFAULT NULL, project_id INT(11) DEFAULT NULL, stock_id INT(11) DEFAULT NULL, gid INT(11) DEFAULT NULL, PRIMARY KEY (`seq`), KEY edx (entryid), KEY ldx (stock_id) ) ENGINE=INNODB;
INSERT INTO stock_seq_table ( entryid, project_id, stock_id, GID ) SELECT CAST(uniquename AS UNSIGNED) AS entryid, s.project_id, s.stock_id, dbxref_id FROM stock s WHERE project_id IN (SELECT project_id FROM stock s GROUP BY project_id HAVING MAX(CAST(uniquename AS UNSIGNED))<>COUNT(stock_id));
UPDATE stock s, (SELECT stocklist.project_id, val, ((((max_seq - stocklist.seq)-(max_seq-min_seq))*(-1))+1) AS list_seq , cnt, stock_id FROM (SELECT project_id , MAX(seq) AS max_seq,MIN(seq) min_seq,MAX(entryid) max_val,MIN(entryid) AS min_val, COUNT(*) AS cnt FROM stock_seq_table GROUP BY project_id ) AS minmaxlist INNER JOIN (SELECT project_id,seq, entryid AS val, stock_id FROM stock_seq_table) AS stocklist ON stocklist.project_id = minmaxlist.project_id ORDER BY seq ) AS Q SET s.uniquename = Q.list_seq WHERE s.project_id = Q.project_id AND s.stock_id = Q.stock_id AND CAST(s.uniquename AS UNSIGNED)<>list_seq;
DROP TABLE IF EXISTS stock_seq_table;
END;
