changeSet.sql:
ALTER TABLE project ADD COLUMN start_date VARCHAR(8), ADD INDEX start_date_fk_idx (start_date ASC);
ALTER TABLE project ADD COLUMN end_date VARCHAR(8), ADD INDEX end_date_fk_idx (end_date ASC);
ALTER TABLE project ADD COLUMN study_update VARCHAR(8), ADD INDEX study_update_fk_idx (study_update ASC);
CREATE TABLE projectprop_dates_bkp ( projectprop_id INT(11) NOT NULL AUTO_INCREMENT, project_id INT(11) NOT NULL, type_id INT(11) NOT NULL, value VARCHAR(255) DEFAULT NULL, rank INT(11) NOT NULL DEFAULT '0', variable_id INT(11) NOT NULL, alias VARCHAR(255) DEFAULT NULL, PRIMARY KEY (projectprop_id), UNIQUE KEY projectprop_idx1 (project_id, type_id, rank, variable_id) );
INSERT INTO projectprop_dates_bkp (SELECT * FROM projectprop WHERE variable_id in (8050, 8060, 8009));
UPDATE project p INNER JOIN projectprop pp ON p.project_id = pp.project_id SET p.START_DATE = substr(PP.VALUE, 1, 8) WHERE pp.variable_id = 8050 AND p.name NOT LIKE '%-ENVIRONMENT%' AND p.name NOT LIKE '%-PLOTDATA%';
UPDATE project p INNER JOIN projectprop pp ON p.project_id = pp.project_id SET p.END_DATE = substr(PP.VALUE, 1, 8) WHERE pp.variable_id = 8060 AND p.name NOT LIKE '%-ENVIRONMENT%' AND p.name NOT LIKE '%-PLOTDATA%';
UPDATE project p INNER JOIN projectprop pp ON p.project_id = pp.project_id SET p.STUDY_UPDATE = substr(PP.VALUE, 1, 8) WHERE pp.variable_id = 8009 AND p.name NOT LIKE '%-ENVIRONMENT%' AND p.name NOT LIKE '%-PLOTDATA%';
DELETE FROM projectprop WHERE variable_id = 8050; DELETE FROM cvterm_relationship WHERE subject_id = 8050; DELETE FROM cvterm WHERE cvterm_id = 8050;
DELETE FROM projectprop WHERE variable_id = 8060; DELETE FROM cvterm_relationship WHERE subject_id = 8060; DELETE FROM cvterm WHERE cvterm_id = 8060;
DELETE FROM projectprop WHERE variable_id = 8009; DELETE FROM cvterm_relationship WHERE subject_id = 8009; DELETE FROM cvterm WHERE cvterm_id = 8009;

