changeSet.sql:
CREATE PROCEDURE deleteTermIdFromProjectPropTable(termId int)
BEGIN
	DECLARE projectId INT;
	DECLARE done INT DEFAULT FALSE;
	DECLARE cur1 CURSOR FOR SELECT project_Id FROM projectprop WHERE variable_id = 8269 order by rank;
	DECLARE CONTINUE HANDLER FOR NOT FOUND SET done = TRUE;

	OPEN cur1;
		read_loop: LOOP FETCH cur1 INTO projectId;
			set @newRank = 0;
			IF done THEN
				LEAVE read_loop;
			END IF;

			DELETE FROM projectprop WHERE project_id = projectId AND variable_id = termId;
			UPDATE projectprop set rank= @newRank:=@newRank+1 WHERE project_id = projectId order by rank;

		END LOOP read_loop;
	CLOSE cur1;
END

preConditions.sqlCheck:
SELECT COUNT(*)
	FROM information_schema.routines
		WHERE routine_schema = DATABASE() AND specific_name = 'deleteTermIdFromProjectPropTable';

