changeSet.sql:
# Insert INSERT CHILD PERMISSIONS FROM  GERMPLASM  AND MANAGE_GERMPLASM

SET @import_germplasm_permission_id = (SELECT permission_id FROM permission where name = 'IMPORT_GERMPLASM');

INSERT INTO role_permission(role_id,permission_id)
SELECT DISTINCT(rp.role_id), @import_germplasm_permission_id FROM role_permission rp INNER JOIN permission p ON rp.permission_id = p.permission_id
	WHERE p.name in ('GERMPLASM', 'MANAGE_GERMPLASM')
	GROUP BY rp.role_id;

